mapper.select:
SELECT * FROM ( SELECT l.branch_id, l.lesson_id, l.thumbnail_url, l.target, l.title, l.cnt, l.cost, m.name tutor_name, CASE WHEN l.cnt = 1 THEN l.start_date || ' ' || l.start_time || '~' || l.end_time ELSE '매주 ' || l.day || ' ' || l.start_time || '~' || l.end_time END AS lesson_time, ROWNUM as rnum FROM lesson l JOIN member m ON l.member_id = m.member_id
l.branch_id = #{branchId}
and l.title like '%' || #{lessonTitle} || '%'
and m.name like '%' || #{tutorName} || '%'
and l.day = #{day}
and l.target = #{target}
and l.category_id = #{categoryId}
and substr(l.start_time, 1, 2) = #{startTime}
and substr(l.end_time, 1, 2) = #{endTime}
and l.cnt = 1
and l.cnt between 2 and 4
and l.cnt >= 5
and TO_DATE(l.end_date, 'YYYY-MM-DD') > SYSDATE and l.deleted_at is null
ORDER BY l.lesson_id desc ) WHERE 12 >= ROWNUM

SELECT /*+ INDEX(s PK_SHORT_FORM) */ s.short_form_id, l.lesson_id, l.title lesson_title, m.name tutor_name, s.name short_form_name, s.thumbnail_url, s.video_url, l.target FROM short_form s JOIN lesson l ON s.lesson_id = l.lesson_id JOIN member m ON l.member_id = m.member_id WHERE s.deleted_at IS NULL AND s.expire_date > sysdate
AND l.branch_id = #{branchId}
ORDER BY s.view_count DESC

mapper.insert:
INSERT INTO
    cart (cart_id, member_id, lesson_id, online_yn)
SELECT
    cart_seq.nextval, #{memberId}, #{lessonId}, #{onlineYn}
FROM
    dual
WHERE
    not exists (
            SELECT 1
            FROM cart
            WHERE lesson_id = #{lessonId}
              AND member_id = #{memberId}
    )

mapper.update:
UPDATE
    short_form
SET
    view_count = view_count + #{viewCount}
WHERE
    short_form_id = #{shortFormId}
